import io.swagger.annotations.ApiOperation;

/**
 * 操作日志操作
 * 
 * @author qinsc (mailto:qinsc@primeton.com)
 *
 */
@RestController
@RequestMapping(value = API_PATH_PREFIX + "/operation-logs", consumes = { APPLICATION_JSON_UTF8_VALUE }, produces = { APPLICATION_JSON_UTF8_VALUE })
public class OperationLogController {
	@Autowired
	private OperationLogService operLogSvc;

	@ApiOperation("分页查询操作日志")
	@RequestMapping(value = "/logs", method = GET, consumes = ALL_VALUE)
	@BoundFunction(OPT_LOG_SEARCH)
	public Page<OperationLog> pagingOperationLogs(@Validated OperationLog.Criteria criteria, @Validated CoframePageRequest pageRequest) { // 方法名前加m, 以使方法进入权限拦截范围内
		return operLogSvc.findByCriteria(criteria, pageRequest.createPageable());
	}

	@ApiOperation("获取操作日志详细")
	@RequestMapping(value = "/logs/{logId}", method = GET, consumes = ALL_VALUE)
	@BoundFunction(OPT_LOG_DETAIL_GET)
	public OperationLogDetail getOperationLogDetail(@PathVariable("logId") String logId) {
		return operLogSvc.getDetailById(logId);
	}
}
